$(document).ready(function(){
  $("#wishlist").on("click","a",function(i){
    i.preventDefault(),
    $("main iframe").attr("src","wishlist/")
  }),
  $("#cart").on("click","a",function(i){
    i.preventDefault(),
    $("main iframe").attr("src","shopping-cart/")
  }),
  $("#section ul").on("click","li",function(){
    $(this).siblings().removeClass("active"),
    $(this).addClass("active"),
    $("main iframe").attr("src",$(this).attr("data-iframe"))
  })
});
